attempts.cypher:
MATCH (s:Service)
WHERE s.metadata.namespace = 'litmus'
AND NOT EXISTS {
  (s)-[:Manages]->(:EndpointSlice)
}
RETURN s.metadata.name AS ServiceName, s.metadata.namespace AS Namespace

