MATCH (p:Pod)-[:BelongsTo]->(ns:Namespace)
WHERE ns['metadata']['name'] = 'storefront'
  AND NOT EXISTS { (d:Deployment)-[:Manages]->(:ReplicaSet)-[:Manages]->(p) }
  AND NOT EXISTS { (ss:StatefulSet)-[:Manages]->(p) }
  AND NOT EXISTS { (ds:DaemonSet)-[:Manages]->(p) }
  AND NOT EXISTS { (j:Job)-[:Manages]->(p) }
  AND NOT EXISTS { (rs:ReplicaSet)-[:Manages]->(p) }
RETURN p['metadata']['name'] AS pod_name, p['metadata']['namespace'] AS namespace

