MATCH (ns:Namespace {metadata: {name: 'storefront'}})<-[:BelongsTo]-(p:Pod)
WHERE NOT EXISTS {
  (p)<-[:Manages]-(:Deployment)
}
AND NOT EXISTS {
  (p)<-[:Manages]-(:StatefulSet)
}
AND NOT EXISTS {
  (p)<-[:Manages]-(:DaemonSet)
}
AND NOT EXISTS {
  (p)<-[:Manages]-(:Job)
}
AND NOT EXISTS {
  (p)<-[:Manages]-(:ReplicaSet)
}
RETURN p.metadata.name AS podName
